import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [users, setUsers] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  // L·∫•y danh s√°ch ng∆∞·ªùi d√πng
  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/users");
      setUsers(res.data);
    } catch (err) {
      console.error("L·ªói khi l·∫•y d·ªØ li·ªáu:", err);
    }
  };

  // G·ªçi khi component mount
  useEffect(() => {
    fetchUsers();
  }, []);

  // Validation + th√™m ng∆∞·ªùi d√πng
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name.trim()) {
      alert("‚ö†Ô∏è Name kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng");
      return;
    }
    if (!/\S+@\S+\.\S+/.test(email)) {
      alert("‚ö†Ô∏è Email kh√¥ng h·ª£p l·ªá");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/users", { name, email });
      setName("");
      setEmail("");
      fetchUsers();
      alert("‚úÖ Th√™m ng∆∞·ªùi d√πng th√†nh c√¥ng!");
    } catch (err) {
      console.error("L·ªói khi th√™m:", err);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("X√≥a ng∆∞·ªùi d√πng n√†y?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/users/${id}`);
      fetchUsers();
    } catch (err) {
      console.error("L·ªói khi x√≥a:", err);
    }
  };

  return (
    <div style={{ padding: 30 }}>
      <h1>üìã Qu·∫£n l√Ω ng∆∞·ªùi d√πng</h1>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="T√™n"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button type="submit">Th√™m</button>
      </form>

      <h2>Danh s√°ch ng∆∞·ªùi d√πng</h2>
      <ul>
        {users.map((user) => (
          <li key={user._id}>
            {user.name} - {user.email}{" "}
            <button onClick={() => handleDelete(user._id)}>‚ùå X√≥a</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
