import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [users, setUsers] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  // L·∫•y danh s√°ch ng∆∞·ªùi d√πng
  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/users");
      setUsers(res.data);
    } catch (err) {
      console.error("‚ùå L·ªói khi t·∫£i danh s√°ch:", err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // Th√™m user m·ªõi
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name.trim()) {
      alert("‚ö†Ô∏è T√™n kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng!");
      return;
    }

    if (!/\S+@\S+\.\S+/.test(email)) {
      alert("‚ö†Ô∏è Email kh√¥ng h·ª£p l·ªá!");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/users", { name, email });
      setName("");
      setEmail("");
      fetchUsers();
    } catch (err) {
      console.error("‚ùå L·ªói khi th√™m ng∆∞·ªùi d√πng:", err);
    }
  };

  // X√≥a user
  const handleDelete = async (id) => {
    if (window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a ng∆∞·ªùi d√πng n√†y?")) {
      try {
        await axios.delete(`http://localhost:5000/api/users/${id}`);
        fetchUsers();
      } catch (err) {
        console.error("‚ùå L·ªói khi x√≥a:", err);
      }
    }
  };

  return (
    <div style={{ padding: "30px", fontFamily: "Arial" }}>
      <h1>üìã Qu·∫£n l√Ω ng∆∞·ªùi d√πng</h1>

      <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="T√™n"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button type="submit">Th√™m</button>
      </form>

      <h2>Danh s√°ch ng∆∞·ªùi d√πng</h2>
      <ul>
        {users.map((u) => (
          <li key={u._id}>
            {u.name} - {u.email}{" "}
            <button onClick={() => handleDelete(u._id)}>‚ùå X√≥a</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
