      alert("‚ö†Ô∏è T√™n kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng!");
      return;
    }
    if (!/\S+@\S+\.\S+/.test(email)) {
      alert("‚ö†Ô∏è Email kh√¥ng h·ª£p l·ªá!");
      return;
    }

    try {
      // üîπ G·ª≠i request l√™n backend MongoDB
      await axios.post("http://localhost:3000/api/users", { name, email });

