      alert("‚ùå Th√™m ng∆∞·ªùi d√πng th·∫•t b·∫°i! (Ki·ªÉm tra backend MongoDB)");
    }
  };

  return (
    <div style={{ marginBottom: "20px" }}>
      <h2>Th√™m ng∆∞·ªùi d√πng (MongoDB)</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="T√™n"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={{ marginRight: "10px" }}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ marginRight: "10px" }}
        />
        <button type="submit">Th√™m</button>
      </form>
    </div>
  );
}

export default AddUser;
